import React from 'react';
import { Card } from '@mui/material';

const UserCard = (props) => {
  return (
    <div className="card">
      <Card>
        <div>
          <img src={props.img} className='avatar' alt='User Avatar' />
        </div>
        <div className="user-info">
          <h2>{props.name}</h2>
          <p>{props.bio}
            <a className="user-name" href={props.link}>
              @{props.userName}
            </a>
          </p>
          <ul>
            <li>{props.repos}<strong>Repos</strong></li>
            <li>{props.followers}<strong>Followers</strong></li>
            <li>{props.following}<strong>Following</strong></li>
          </ul>
        </div>
      </Card>
    </div>
  )
}
export default UserCard;
